import 'dart:math';
import 'package:flutter/material.dart';
import '../models/Product.dart';
import '../screens/ProductDetails.dart';
import 'ProductTile.dart';

class ProductsWidget extends StatelessWidget
{
    final List<Product> products = [];
    final random = Random();

    ProductsWidget({Key? key}) : super(key: key)
    {
        String desc = "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate";
        products.add(Product(
                name: 'T-shirt',
                description: desc,
                price: 100 + random.nextInt(100),
                imgURL:
                'data:image/jpeg;base64,'));
        products.add(Product(
                name: 'Hoodie',
                description: desc,
                price: 100 + random.nextInt(100),
                imgURL:
                'data:image/jpeg;base64,'));
        products.add(Product(
                name: 'Cap',
                description: desc,
                price: 100 + random.nextInt(100),
                imgURL:
                'data:image/jpeg;base64,'));
        products.add(Product(
                name: 'Jacket',
                description: desc,
                price: 100 + random.nextInt(100),
                imgURL:
                'data:image/jpeg;base64,'));
    }

    @override
    Widget build(BuildContext context)
    {
        return GridView.builder(
            gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 4,
                crossAxisSpacing: 10,
                mainAxisSpacing: 10,
                childAspectRatio: 0.75),
            itemCount: this.products.length,
            itemBuilder: (context, index)
            {
                return GestureDetector(
                    onTap: ()
                    {
                        Navigator.push(
                            context,
                            MaterialPageRoute(
                                builder: (context) =>
                                ProductDetails(product: this.products[index])));
                    },
                    child: ProductTile(product: this.products[index]));
            }
        );
    }
}
